problem_definition.command_line:
make clean-run; rm -f 8.log 16.log 32.log 64.log &&
      mpiexec ../../bin/flredecomp -i 1 -o 2 -v -l geopressure_8 geopressure_8_flredecomp &&
      mpiexec ../../bin/flredecomp -i 1 -o 2 -v -l geopressure_16 geopressure_16_flredecomp &&
      mpiexec ../../bin/flredecomp -i 1 -o 2 -v -l geopressure_32 geopressure_32_flredecomp &&
      mpiexec ../../bin/flredecomp -i 1 -o 2 -v -l geopressure_64 geopressure_64_flredecomp &&
      mpiexec ../../bin/fluidity -v2 geopressure_8_flredecomp.flml > 8.log;
      mpiexec ../../bin/fluidity -v2 geopressure_16_flredecomp.flml > 16.log;
      mpiexec ../../bin/fluidity -v2 geopressure_32_flredecomp.flml > 32.log;
      mpiexec ../../bin/fluidity -v2 geopressure_64_flredecomp.flml > 64.log;
