import vtktools
import numpy

for i in range(1, len(time), 2):
   filename='mphase_rogue_shock_tube_dense_bed_glass_' + str(i) + '.vtu'
   vt=vtktools.vtu(filename)

   if(time[i] < -0.0005):
      probedpressure_upstream = vtktools.vtu.ProbeData(vt, numpy.array([[0.0125, 1.35 - 0.11, 0]]), 'Gas::Pressure')
      assert abs(probedpressure_upstream - 100000) < 1.0e-2
   else:
      break

import vtktools
import numpy

for i in range(1, len(time), 2):
   filename='mphase_rogue_shock_tube_dense_bed_glass_' + str(i) + '.vtu'
   vt=vtktools.vtu(filename)

   if(time[i] > -0.0005 and time[i] < 0.0001):
      probedpressure_upstream = vtktools.vtu.ProbeData(vt, numpy.array([[0.0125, 1.35 - 0.11, 0]]), 'Gas::Pressure')
      assert abs(probedpressure_upstream) < 190000
      assert abs(probedpressure_upstream) > 100000
   else:
      if(time[i] > 0.0001):
         break

