pass_tests.test:
for t in range(0,len(time)):
   if(time[t] > 30.0):
      assert tephra_velocity_max[0:t-1].all() < 0.0025
      break

for t in range(0,len(time)):
   if(time[t] > 30.0):
      assert min(tephra_velocity_max[t:]) > 0.002
      assert max(tephra_velocity_max[t:]) < 0.05
      break
