from fluidity_tools import stat_parser import numpy as np
s = stat_parser('particle_attributes.particles.Chris') attributes_error_3 = np.zeros((1,100)) X = np.fromfile('Xvals.txt',sep=' ') Y = np.fromfile('Yvals.txt',sep=' ')
for i in range(100): name = 'Chris_' + str(i+1).zfill(3) attributes_error_3[0,i] = s[name]['Chem3'][0][-1] - (s[name]['position'][0][-1] + s['ElapsedTime']['value'][-1]) print "max error",abs(attributes_error_3).max()

