from fluidity_tools import stat_parser import numpy as np
s = stat_parser('particle_attributes_zoltan.particles.Chris') attributes_error = np.zeros((1,100))
for i in range(100): name = 'Chris_' + str(i+1).zfill(3) attributes_error[0,i] = s[name]['Chem3'][0][-1] - (s[name]['position'][0][-1] + s['ElapsedTime']['value'][-1]) attributes_error_3=abs(attributes_error).max() print ("max error",abs(attributes_error_3).max())

