import numpy as np import h5py from fluidity_tools import stat_parser
f = h5py.File('particle_attributes.particles.DtMeasure.h5part', 'r') z = stat_parser("particle_attributes.stat") dt_errors = np.zeros(10,float)
for i in range(10): d = f['/Step#{}'.format(len(f)-(10-i))] val = d['DtVal'][:] dt_errors[i] = val- z['dt']['value'][i+1] dt_error=abs(dt_errors).max() print ("max error",dt_error)

import numpy as np import h5py from fluidity_tools import stat_parser
f = h5py.File('particle_attributes.particles.DtMeasure.h5part', 'r') z = stat_parser("particle_attributes.stat") dt_change_errors = np.zeros(9,float)
for i in range(1,10): d = f['/Step#{}'.format(len(f)-(10-i))] val2 = d['Dtchange'][:] dt_change_errors[i-1] = val2 - (z['dt']['value'][i+1] - z['dt']['value'][i]) dt_change_error=abs(dt_change_errors).max() print ("max error",dt_change_error)
