problem_definition.command_line:
no_proc=2
filename='periodic_2plus1_parallel';
mesh='MMS_B';
periodic_mesh='PeriodicMesh';

#periodise the flml
../../bin/periodise ${filename}.flml &&

#flredecomp the flml
mpiexec -n 2 ../../bin/flredecomp -v -l -i 1 -o $no_proc ${filename}_periodised ${filename}_periodised_flredecomp &&

#run
mpiexec -n 2 ../../bin/fluidity -v3 -l ${filename}_periodised_flredecomp.flml
